package create2

import (
	"fmt"
	"tron/utils"
)

func TestCalculateAddress() {
	ethAddress, err := CalculateAddress("TVj8pj636Y2HoUhrt8Q7wtvXjLb6qX9Tad", 123, "")
	if err != nil {
		fmt.Printf("Error :: %v", err)
	}

	fmt.Printf("Eth Address:: %s\n", ethAddress)

	tronAddress, err := utils.EthToTronAddress(ethAddress.String())
	if err != nil {
		fmt.Printf("Error :: %v", err)
	}

	fmt.Printf("Tron Address:: %s\n", tronAddress)
}
